const items = $input.all();

if (items.length === 0) {
  return [{ json: { hasData: false } }];
}

const dateLabel = new Date().toLocaleDateString('ru-RU', { timeZone: 'Europe/Moscow' });
const MAX_CHARS = 50000;
const sections = [];
let totalLen = 0;
let truncated = false;

for (const item of items) {
  const leadId = item.json.lead_id || 'unknown';
  const transcript = item.json.transcript_text || '';
  const section = `LEAD-${leadId}:\n${transcript}`;

  if (totalLen + section.length > MAX_CHARS) {
    truncated = true;
    break;
  }
  sections.push(section);
  totalLen += section.length;
}

const combined = sections.join('\n\n---\n\n') + (truncated ? '\n\n[...обрезано, слишком много текста за день...]' : '');
const rowIds = items.slice(0, sections.length).map((item) => item.json.id);
const leadIds = Array.from(new Set(items.slice(0, sections.length).map((item) => item.json.lead_id).filter((v) => v !== null && v !== undefined)));

return [{
  json: {
    hasData: true,
    dateLabel,
    combined,
    rowIds,
    leadIds,
    count: sections.length,
    truncated
  }
}];

const meta = $('Aggregate Transcripts').first().json;
const content = ($json.choices && $json.choices[0] && $json.choices[0].message && $json.choices[0].message.content) ? $json.choices[0].message.content : '';
const summaryText = content.trim() || 'Сводка не получена.';
const leadList = Array.isArray(meta.leadIds) && meta.leadIds.length > 0 ? meta.leadIds.map((id) => `LEAD-${id}`).join(', ') : '—';
const header = `Ежедневный дайджест за ${meta.dateLabel}\nВстреч: ${meta.count}\nКлиенты: ${leadList}`;
const digest = `${header}\n\n${summaryText}`.trim();

return [{
  json: {
    digest,
    summaryText,
    rowIds: meta.rowIds
  }
}];

const text = $json.digest || '';
const max = 3500;
const chunks = [];
for (let i = 0; i < text.length; i += max) {
  chunks.push(text.slice(i, i + max));
}

return chunks.map((chunk, index) => ({
  json: {
    text: chunks.length > 1 ? `${chunk}\n\n(часть ${index + 1}/${chunks.length})` : chunk,
    part: index + 1,
    total: chunks.length
  }
}));

const meta = $('Build Digest').first().json;
const rowIds = Array.isArray(meta.rowIds) ? meta.rowIds : [];
const rowIdsSql = rowIds.length > 0 ? rowIds.join(',') : '-1';
const summaryEscaped = (meta.summaryText || '').replace(/'/g, "''");

return [{
  json: {
    rowIdsSql,
    summaryEscaped
  }
}];
